function processCharacter(char) {
  let lowerChar = char.toLowerCase();
  const notes = {
    a: ['C4', 'E4', 'G4'],
    b: ['D4', 'F4', 'A4'],
    c: ['E4', 'G4', 'B4'],
    d: ['F4', 'A4', 'C5'],
    e: ['G4', 'B4', 'D5'],
    f: ['A4', 'C5', 'E5'],
