    n: ['B3', 'D4', 'F4', 'A4'],
    o: ['C4', 'E4', 'G4', 'B4'],
    p: ['D4', 'F4', 'A4', 'C5'],
    q: ['E4', 'G4', 'B4', 'D5'],
    r: ['F4', 'A4', 'C5', 'E5'],
    s: ['G4', 'B4', 'D5', 'F5'],
    t: ['A4', 'C5', 'E5', 'G5'],
    u: ['B3', 'D4', 'F4', 'Ab4'],
    v: ['C4', 'D4', 'G4'],
    w: ['D4', 'E4', 'A4'],
    x: ['E4', 'F4', 'B4'],
    y: ['F4', 'G4', 'A4'],
    z: ['G4', 'A4', 'B4']
  };

  const colors = {
    a: [255, 102, 102],
