    e: ['G4', 'B4', 'D5'],
    f: ['A4', 'C5', 'E5'],
    g: ['B3', 'D4', 'F4'],
    h: ['C4', 'F4', 'G4'],
    i: ['D4', 'G4', 'A4'],
    j: ['E4', 'A4', 'B4'],
    k: ['F4', 'A4', 'D5'],
    l: ['G4', 'B4', 'E5'],
    m: ['A3', 'C4', 'E4', 'G4'],
    n: ['B3', 'D4', 'F4', 'A4'],
    o: ['C4', 'E4', 'G4', 'B4'],
    p: ['D4', 'F4', 'A4', 'C5'],
