function cheek() {
  //cheek

  //cheek color
  from = color(248, 199, 172);
  to = color("rgb(241,61,61)");
  let interA = lerpColor(from, to, t);

  if (
    (mouseX < 340 && mouseX > 270 && mouseY > 350 && mouseY < 410) ||
    (mouseX > 460 && mouseX < 520 && mouseY > 350 && mouseY < 410)
  ) {
    if (t < 1) {
      t += 0.01;
    }
  } else if (t > 0) {
    t -= 0.01;
  }

  fill(interA);
  noStroke();
  circle(310, 380, 60);
  circle(490, 380, 60);
}
